import java.util.Hashtable;;

public class HashTable {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	
		Hashtable<Integer, String> Ht = new Hashtable<Integer,String>();
		
		Ht.put(0, "Raj");
		Ht.put(1, "Ravi");
		Ht.put(2, "Ram");
		Ht.put(3, "Ria");
		Ht.put(4, "Maya");
		System.out.println(Ht);	
		System.out.println();
		System.out.println();
		
	}

}
